router.post('/chat', async (req, res) => {
    const { message, lang } = req.body;
    let userMessage = message;
    let targetLang = lang || 'en';

    try {
        // Translate to English if not already
        if (targetLang !== 'en') {
            const translation = await translate(message, { to: 'en' });
            userMessage = translation.text;
        }

        // Logic for generating response (expanded)
        let reply = "I'm not sure about that. Can you ask about prices, loans, or schemes?";
        const lowerMsg = userMessage.toLowerCase();

        if (lowerMsg.includes('price') || lowerMsg.includes('rate') || lowerMsg.includes('mandi')) {
            reply = "You can check the Live Price page for the latest mandi prices for various crops.";
        } else if (lowerMsg.includes('loan') || lowerMsg.includes('credit') || lowerMsg.includes('kcc')) {
            reply = "We have several loan schemes available like Kisan Credit Card. Visit the Smart Loan page for details.";
        } else if (lowerMsg.includes('subsidy') || lowerMsg.includes('scheme') || lowerMsg.includes('government')) {
            reply = "There are many government schemes like PM-KISAN and Fasal Bima Yojana. You can find details in the Smart Loans section.";
        } else if (lowerMsg.includes('farm') || lowerMsg.includes('farming') || lowerMsg.includes('crop')) {
            reply = "For farming advice, it's best to consult with a local KVK expert. I can help you find schemes related to your crop.";
        } else if (lowerMsg.includes('hello') || lowerMsg.includes('hi') || lowerMsg.includes('namaste')) {
            reply = "Namaste! How can I help you with your farming needs today?";
        }

        // Translate reply back to user's language
        if (targetLang !== 'en') {
            const replyTranslation = await translate(reply, { to: targetLang });
            reply = replyTranslation.text;
        }

        res.json({ reply });
    } catch (error) {
        console.error('Translation error:', error);
        // Fallback if translation fails
        res.json({ reply: "I'm having trouble understanding. Please try again." });
    }
});

module.exports = router;
